import './ExpenseItem.css';
function ExpenseItem(expenses) {
    const date = expenses.date;
    const title =expenses.title;
    const amount = expenses.amount;
    return (
            
            <div className='expense-item'>
                <div>
                    <expenseDate dateS={date}/>
                </div>
                <div className='expense-item__description'>
                    <h2>{props.title}</h2>
                    <div className='expense-item__price'>â‚¹{props.amount}</div>
                </div>
            </div>
            
        
    );
}
const expenseDate = (props)=> {
    const date = new date(props.dateS);
    const month = date.toLocalString('en-US', {month:'long'});
    const day = date.toLocalString('en-US', {month:'2-digit'});
    const year = date.getFullYear();

    return (
        <div className='expense-date'>
            <div className='expense-date_month'>{month}</div>
            <div className='expense-date_month'>{day}</div>
            <div className='expense-date_month'>{year}</div>
        </div>
    )
}

export default ExpenseItem;
